function IMC(){const form=document.querySelector(".form"),finalResult=document.querySelector(".result"),alert=document.querySelector(".alert");form.addEventListener("submit",event=>{event.preventDefault();const weight=form.querySelector(".weight").value,height=form.querySelector(".height").value;if(0==height)return alert.innerHTML="<span class='alertSpan'>Preencha todos os campos  *</span>";{const imcResult=weight/(height*height);function result(){return imcResult<=18.5?"Abaixo do Peso":imcResult>=18.6&&imcResult<=24.9?"Com o peso normal":imcResult>=25&&imcResult<=29.9?"Com sobrepeso":imcResult>=30&&imcResult<=34.9?"Com Obesidade 1° grau":imcResult>=35&&imcResult<=39.9?"Com Obesidade 2° grau":"Com Obesidade 3° grau"}const value=`<div class="imcblock"><h4>Seu IMC é:</h4><p>${imcResult.toFixed(2)}</p></div<div class="imcblock"><h4>Você está:</h4><p>${result()}</p></div>`;finalResult.innerHTML=value}})}IMC();